package com.syntax.reviewclass2;

public class IfElseDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 3;
		
		if (num>5) {
			System.out.println("Number is greater than 5"); //If condition is true, it will print this statement
		}
		
		if (true) { // If statement must be a boolean
			System.out.println("True");
		}
	}

}
